function createText(){ajaxReq("newText","",function(responseText){document.location=this.responseText})}function loadTextOverview(){ajaxReq("loadTextOverview","",function(responseText){$("#textOverview").html(responseText)}),renderTextOverview()}function renderTextOverview(){updateTextOverview(),orderTextOverview()}function updateTextOverview(){var query=$("#textFilter").val().trim();0!=(query=query.toLowerCase()).length?$(".textlink").each(function(i,item){-1!=$(item).text().toLowerCase().indexOf(query)?$(item).show():$(item).hide()}):$(".textlink").show()}function orderTextOverview(){var order=$("#textOrder").val().trim().toLowerCase(),items=$(".textlink").get(),orderClass=order.startsWith("title")?".loaderTitle":order.startsWith("name")?".loaderName":".loaderAuthor";items.sort(function(a,b){var textA=$(a).find(orderClass).text(),textB=$(b).find(orderClass).text();return textA<textB?1:textB<textA?-1:0}),-1==order.indexOf("desc")&&items.reverse(),$.each(items,function(i,item){$("#textOverview").append(item)})}$(document).ready(function(){loadTextOverview()});